      {{text $Item.DocumentComments}}


  {{- end}}


    {{- range $index, $country := $Item.Citizenship.props.value}}

      {{ $country }}

    {{- end}}



    {{branch $Item.IsDeceased}}




  {{- if and (not $usCitizen) (not $deceased) }}


      {{branch $Item.HasAffiliation}}





